mapper.update:
UPDATE BOARD
SET TITLE = #{title}
  , CONTN = #{contn}
WHERE BBS_SEQ = #{bbsSeq}

mapper.insert:
INSERT INTO ATTCH
(
      BBS_SEQ
    , ATTACH_FILE_NM
    , SAVE_FILE_NM
    , FILE_PATH
    , USE_YN
)
VALUES
    (
          #{bbsSeq}
        , #{attachFileNm}
        , #{saveFileNm}
        , #{filePath}
        , 'Y'
    )

mapper.delete:
DELETE from ATTCH
where BBS_SEQ = #{bbsSeq}
AND
ATTACH_FILE_SEQ NOT IN

    #{arr}

mapper.update:
/*BoardMapper.notiViewCountUpdate*/
UPDATE BOARD
SET INQ_CNT = INQ_CNT + 1
WHERE BBS_SEQ = #{bbsSeq}

mapper.select:
